//4칸 슬라이드 배너
var fourSlide = function(){
	this.csSlide = $(".csSlide");

	//상품수가 4의배수가 아닐 경우 앞의 상품 채워넣기
	$(".csSlide > ul").each(function(){
		this.addNum = 0;
		this.dlBoxNum = $(this).find("li").length;

		if(this.dlBoxNum % 4 != 0){
			this.addNum = (4 - this.dlBoxNum % 4);
			for(var i=0;i<this.addNum;i++){
				this.addBox = $(".csSlide > ul").eq(0).find("li").eq(i).clone();
				this.addBox.appendTo($(this));
			}
		}
	});
	$(".csSlide").show(); //slick 실행 전 hide시켰던 요소 보이기

	$(".csSlide").slick({
		autoplay: false,
		autoplaySpeed: 3000,
		infinite: true,
		centerMode: false,
		slidesToShow: 1,
		slidesToScroll: 1,
		arrows: false,
		asNavFor: null,
		dots: true,
		dotsClass: 'stat',
		draggable: true,
		variableWidth: false
	});
}

