//상품 수량 수정
var setUnit = function(unit_id, upNdown){
	if($("#" + unit_id).prop("tagName") == "INPUT"){
		var pUnit = parseInt($("#" + unit_id).val());

		if (upNdown == 1) { //1 : Up, 2 : Down
			pUnit += 1;
			$("#" + unit_id).val(pUnit);
		} else {
			if (pUnit > 1) {
				pUnit -= 1;
				$("#" + unit_id).val(pUnit);
			}else{
				$("#" + unit_id).val(1);
			}
		}
	}else{
		var pUnit = parseInt($("#" + unit_id).text());

		if (upNdown == 1) { //1 : Up, 2 : Down
			pUnit += 1;
			$("#" + unit_id).text(pUnit);
		} else {
			if (pUnit > 1) {
				pUnit -= 1;
				$("#" + unit_id).text(pUnit);
			}else{
				$("#" + unit_id).text(1);
			}
		}
	}
	return false;
};

