//별점체크
var chkStar = {
	init:function(clickObj){
		this.useChkStar = clickObj.parent().parent();
		this.touchStar = clickObj;
		this.touchStar.off("click");
		this.touchStarWid = this.touchStar.parent().width() / 5;
		this.starArr = new Array;
		this.starPos = this.touchStar.parent().offset().left;
		for(var i = 0;i < 5;i++){
			this.starArr[i] = Math.ceil((this.starPos + (this.touchStarWid * (i + 1)))); //별점 칸수별 위치 저장
		}
		chkStar.addEvent();
	},
	addEvent:function(){
		$(".b_starWrap").off("click").on("click",function(e){
			chkStar.init($(this).find(".star"));
			chkStar.check($(this).find(".star"),e.pageX);
		});
	},
	check:function(touchObj,touchPos){
		this.lastNum = new Array;
		for( i = 0;i < 5;i++){
			if(this.starPos < touchPos && this.starArr[i] > touchPos){
				this.lastNum.push(i);
			}
		}

		switch(this.lastNum[0]){
			case 0 : 
				this.curNum = 20;
				break;

			case 1 : 
				this.curNum = 40;
				break;

			case 2 : 
				this.curNum = 60;
				break;

			case 3 : 
				this.curNum = 80;
				break;

			case 4 : 
				this.curNum = 100;
				break;
		}

		this.touchStar.css({"width":this.curNum + "%"}).attr("data-num",this.curNum);
	}
};

