//쿠키 생성
function setCookie(name, value, expiredays) {
	var todayDate = new Date();
	todayDate.setDate(todayDate.getDate() + expiredays);
	cookies = name + "=" + escape(value) + "; path=/; expires=" + todayDate.toGMTString() + ";";
	if(typeof expiredays != 'undefined') cookies += ';expires=' + todayDate.toGMTString() + ';';
	document.cookie = cookies;
}

