//메인 TV쇼핑, 오늘의 추천 동영상 플레이
var mainMovie = function(myPlayer,myVod,seekObj,timeObj){
	playBox = document.getElementById(myPlayer);
	seekSlider = document.getElementById(seekObj);
	playVod = document.getElementById(myVod);
	playBox.style.display = "block";
	playVod.play();
	$("#" + myPlayer + " .videoControl").show();
	seekSlider.addEventListener("change",vidSeek,false);
	$("#" + myVod).on(
			"timeupdate", 
			function(event){
				moviePlay.onTrackedVideoFrame(this.currentTime, this.duration, timeObj);
				seekTimeUpdate();
	});
	setTimeout(function(){
		$("#" + myPlayer + " .videoControl").fadeOut(200);
	},1500);
	$("#" + myVod).on(
		"mouseover",function(){$("#" + myPlayer + " .videoControl").fadeIn(100);
	});
	return false;
};

var vidSeek = function(){
	var seekto = playVod.duration * (seekSlider.value / 100);
	playVod.currentTime = seekto;
};

var seekTimeUpdate = function(){
	var nt = playVod.currentTime * (100 / playVod.duration);
	seekSlider.value = nt;
};

var moviePlay = {
	play:function(target,myObj){
		this.playVideo = document.getElementById(target);
		this.playVideo.load();
		this.playVideo.play();
		
		$("#" + myObj + " .btnPlay").hide();
		$("#" + myObj + " .btnPause").show();

		setTimeout(function(){
			$("#" + myObj + " .videoControl").fadeOut(200);
		},1500);
		$("#" + target).on(
			"mouseover",function(){$("#" + myObj + " .videoControl").fadeIn(100);
		});
	},
	onTrackedVideoFrame:function(currentTime, duration, myTimeObj){
		var m = Math.floor(currentTime / 60) < 10 ? '0' + Math.floor(currentTime / 60) : Math.floor(currentTime / 60);
		var s = Math.floor(currentTime - (m * 60)) < 10 ? '0' + Math.floor(currentTime - (m * 60)) : Math.floor(currentTime - (m * 60));

		$("#" + myTimeObj).text(m + ':' + s);
	},
	stop:function(target,myObj){
		this.playVideo = document.getElementById(target);
		this.playVideo.pause();
		$("#" + myObj + " .btnPause").hide();
		$("#" + myObj + " .btnPlay").show();
	},
	volume:function(target,myObj){
		this.playVideo = document.getElementById(target);
		if(this.playVideo.muted == false){
			this.playVideo.muted = true;
			//$(target).find("img").prop("src",$(target).find("img").attr("src").replace("mov_vol","mov_mute"));
			$("#"+ myObj +" .btnVol").find("img").prop("src", $("#"+ myObj +" .btnVol").find("img").attr("src").replace("mov_vol","mov_mute"))
		}else{
			this.playVideo.muted = false;
			//$(target).find("img").prop("src",$(target).find("img").attr("src").replace("mov_mute","mov_vol"));
			$("#"+ myObj +" .btnVol").find("img").prop("src", $("#"+ myObj +" .btnVol").find("img").attr("src").replace("mov_mute","mov_vol"))
		}
	},
	resize:function(target,myObj){
		this.playVideo = document.getElementById(target);
		if(mobile.match("kshopapp")){
			commFunc.playNativeVideo();
		}else{
			if (this.playVideo.webkitEnterFullScreen) {
				this.playVideo.webkitEnterFullScreen();
			} else {
				this.playVideo.webkitRequestFullscreen();
			}
		}
	},
	close:function(target,myObj){
		this.playVideo = document.getElementById(target);
		this.playVideo.load();
		this.playVideo.pause();
		$("#" + myObj).hide();
	}
};

