	$("body").on(eventType, 'input', function(e){
		var newVal = jQuery(this).val();
		
		if(jQuery(this).hasClass('onlyChar')){
			if(!formatUtil.isValidFormat(this, REGEX.AH2)){
        		jQuery(this).val(newVal.substring(0, newVal.length -1));
        	}
		}
		
		if(jQuery(this).hasClass('onlyCharSpace')){
			var REGEX_TYPE = REGEX.AH_SPACE2;
			if(isIOS()) {REGEX_TYPE = REGEX.AH_SPACE2_IOS;}
			
			if(!formatUtil.isValidFormat(this, REGEX_TYPE)){
        		jQuery(this).val(newVal.substring(0, newVal.length -1));
        	}
		}
		
		if(jQuery(this).hasClass('onlyCharSpaceCorp')){
			var REGEX_TYPE2 = REGEX.AH_SPACE_CORP2;
			if(isIOS()) {REGEX_TYPE2 = REGEX.AH_SPACE_CORP2_IOS;}
			
			if(!formatUtil.isValidFormat(this, REGEX_TYPE2)){
        		jQuery(this).val(newVal.substring(0, newVal.length -1));
        	}
		}
		
		if(jQuery(this).hasClass('onlyNum')){
			if(!formatUtil.isValidFormat(this, REGEX.NUMERIC)){
        		jQuery(this).val(newVal.substring(0, newVal.length -1))
        	}
		}
	});
